void setup()
{
  Serial.begin(9600);

  while (!Serial)
  {
    yield();
  }

  Serial.println("Scanning for BLE printer.");

  if (tpScan("SC03h-51E7", 5))
  {
    Serial.println("Found a printer, attempting to connect.");

    if (tpConnect())
    {
      Serial.print("Connected to: '");
      Serial.print(tpGetName());
      Serial.print("' | Width: ");
      Serial.println(tpGetWidth());

      tpSetBackBuffer(buffer, width, height);
      tpSetWriteMode(MODE_WITHOUT_RESPONSE);
      tpAlign(ALIGN_CENTER);
      tpSetEnergy(0xFFFF);
      tpSetQuality(0xFFFF);

      tpDrawText(16, 16, (char *)"Pika!", FONT_LARGE, 1);
      tpPrintBuffer();

      tpLoadBMP(bitmap, 0, 0, 0);
      tpPrintBuffer();
      tpFeed(16);

      Serial.println("Disconnecting.");
      tpDisconnect();
    }
  }
  else
  {
    Serial.println("Could not find printer.");
  }
}

